    public static void testVCode2(String code, String base64) throws Exception {
        CloseableHttpClient httpClient = HttpClients.createDefault();
        RequestBuilder requestBuilder = RequestBuilder.get();
        requestBuilder.setUri(uri);
//        Map<String,String> checkMap = new HashMap<String, String>();
        SimpleDateFormat sdf = new SimpleDateFormat("yyyyMMddHHmmss");
        String ts = sdf.format(new Date());
// chs=MD5（ts+cs+key），key由接口提供方提供。
//        String chs = MD5Util.convert(ts+" ???"+"???");
//        checkMap.put("ts", ts);
//        checkMap.put("cs", "???");
//        checkMap.put("chs", chs);

        requestBuilder.addParameter("checkJSON", getCheckJSON());
        requestBuilder.addParameter("capImageType", "png");//图片类型
//        requestBuilder.addParameter("taskUrl", "http://www.xm-l-tax.gov.cn/");//站点url
        requestBuilder.addParameter("taskUrl", "http://wsbsfwt2.xmtax.gov.cn:8001/");
        requestBuilder.addParameter("fileBase64", base64);//图片base64
        requestBuilder.addParameter("bgtype", "");//背景色
        requestBuilder.addParameter("dataType", "VerificationCode");//接口简称
        HttpUriRequest request = requestBuilder.build();
        CloseableHttpResponse response = httpClient.execute(request);
        String result = IOUtils.toString(response.getEntity().getContent());
        System.out.println("code is:" + code + ", result is:" + result);
        httpClient.close();
//        System.out.println("end");
    }

    private static String getCheckJSON() {
        JSONObject validateJson = new JSONObject();
        try {
            //检验数据拼装(
            validateJson.put("cs", CS);

            Date date = new Date();
            String dateStr = new SimpleDateFormat("yyyyMMddHHmmss").format(date);
            validateJson.put("ts", dateStr);

            //MD5（ts+cs+AppKey）
            String chs = MD5Util.convert(dateStr + CS + APP_KEY);
            validateJson.put("chs", chs);
        } catch (JSONException e) {
            e.printStackTrace();
        }
        return validateJson.toString();
    }

